mapper.select:
select
d.total_price,d.electric, d.water, d.month, d.year, d.room_id
 from detail d
 where d.year = #{year}
 and d.month = #{month}

select
d.total_price,d.electric, d.water, d.month, d.year, d.room_id
 from detail d
 where d.year = #{year}
 and d.month = #{month}
 and d.room_id = #{roomId}

mapper.update:
update detail

    electric=#{electric},
    water=#{water},
    total_price=#{totalPrice},

where room_id=#{roomId}
and year = #{year}
and month = #{month}
